choix = gets.chomp.to_i


while choix != 0
    case choix.to_i
        when 1
            puts "Le @ le plus cours est " +array.min{ |a,b| a.length <=> b.length}.to_s + "."
        when 2
            puts "Le nombre de @ constitué d'exactement 5 caractères est de "+array.count{|x| x.size == 6}.to_s + "."
        when 3
            puts "Les Twittos dont la @ commence par une majuscule sont : "+array.count{|name| name[1,1] >= 'A' && name[1,1] <= 'Z'}.to_s + "."
        when 4
            puts "Voici la liste trié par ordre alphabétique : "+array.sort.to_s + "."
        when 5
            puts "Voici la liste des Twittos trié en fonction de la taille de leur @ "+array.sort_by{|x| x.length}.to_s + "."
        when 6
            puts "Position de @epenser dans la liste : " +array.index('@epenser').to_s + "."
        when 7
            puts "Veuillez choisir une taille :"
            print "> "
            length = gets.chomp.to_i
            get_twittos_length(length)
        else 
            puts "Erreur"
    end

    if choix != 5 && choix != 7 && choix != 4
        puts ""
        puts "|=========> Une autre action :"
        print "             └ "
        choix = gets.chomp.to_i
        puts ""
    else 
        puts "Appuyez sur [ENTREE] pour revenir au menu."
        gets.chomp
        system('clear')
        system('ruby 00_journalists.rb')
        menu 
    end
end




